pragma solidity ^0.8.10;

contract Auth {
    mapping (address => uint256) public wards;

    event Rely(address indexed usr);
    event Deny(address indexed usr);

    function rely(address usr) external auth {
        wards[usr] = 1;
        emit Rely(usr);
